mapper.update:

update task
set name = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR},
  rate = #{rate,jdbcType=INTEGER},
  icon_id = #{iconId,jdbcType=BIGINT},
  reward_id = #{rewardId,jdbcType=BIGINT},
  task_type = #{taskType,jdbcType=INTEGER},
  type = #{type,jdbcType=INTEGER}
  channelId = #{channelId,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
   select t.id id,t.name,(select name from item where id=r.item_id) rewardType,
r.reward_value rewardValue,t.description,t.rate,(select url from icon where t.icon_id=id) 
icon,t.type taskType,(select name from item where id=r.item_id) rewardName from task t  join  reward r on t.reward_id=r.id  where t.task_type=1 and (t.channel_id=1 or channel_id=#{channelId});

    select t.id id,t.name,(select name from item where id=r.item_id) rewardType,
r.reward_value rewardValue,t.description,t.rate,(select url from icon where t.icon_id=id) 
icon,t.type taskType,(select name from item where id=r.item_id) rewardName from task t  join  reward r on t.reward_id=r.id  where t.task_type=3;

    select t.id id,t.name,(select name from item where id=r.item_id) rewardType,
r.reward_value rewardValue,t.description,t.rate,(select url from icon where t.icon_id=id) 
icon,t.type taskType,(select name from item where id=r.item_id) rewardName from task t  join  reward r on t.reward_id=r.id  where t.task_type=2;
