mapper.insert:

insert into user_reward (id, user_account_id, reward_id, 
  state, gain_time, rank
  )
values (#{id,jdbcType=BIGINT}, #{userAccountId,jdbcType=BIGINT}, #{rewardId,jdbcType=BIGINT}, 
  #{state,jdbcType=INTEGER}, #{gainTime,jdbcType=TIMESTAMP}, #{rank,jdbcType=VARCHAR}
  )

mapper.select:
  select t.id id,u.state neckType from reward r left join user_reward u on r.id=u.reward_id 
right join task t on t.reward_id=r.id and u.user_account_id=#{userId}
where t.task_type=1 ;

  select t.id id,u.state neckType from reward r left join user_reward u on r.id=u.reward_id 
right join task t on t.reward_id=r.id and u.user_account_id=#{userId}
where t.task_type=3 ;

  select t.id id,u.state neckType from reward r left join user_reward u on r.id=u.reward_id 
right join task t on t.reward_id=r.id and u.user_account_id=#{userId}
where t.task_type=2 ;
